import 'package:flutter/foundation.dart';import 'package:flutter/material.dart';class CounterScreen extends StatefulWidget{  const CounterScreen({super.key});  @override  State<CounterScreen> createState() {    return _CounterScreenState();  }}class _CounterScreenState extends State<CounterScreen> {  int counter = 1;  @override  void initState() {    super.initState();  }  @override  Widget build(BuildContext context) { return Scaffold(   appBar: AppBar(     title: const Text('Counter App',style:     TextStyle(color: Colors.purple),),     centerTitle: true,     backgroundColor: Colors.pinkAccent,   ),   body: Center(child: Row(     mainAxisAlignment: MainAxisAlignment.center,     children: [       TextButton(           onPressed: (){             setState(() {               counter--;               if (kDebugMode) {                 print('$counter');               }             });           },           child: const Text(         'MINUS',style:           TextStyle(             fontSize: 25,             fontWeight: FontWeight.bold,             color: Colors.purple           ),       )),        Padding(         padding: const EdgeInsets.symmetric (           horizontal: 20         ),         child: Text(             '$counter',         style:  const TextStyle(           fontSize: 50,           fontWeight: FontWeight.bold,           color: Colors.pinkAccent         ),),       ),       TextButton(           onPressed: (){         setState(() {           counter++;           if (kDebugMode) {             print('$counter');           }         });       },           child: const Text(             'PLUS',style: TextStyle(             fontSize: 25,             fontWeight: FontWeight.bold,             color: Colors.purple           ),           ))     ],   )), );  }}